annotations.alm-examples:
[
  {
    "apiVersion": "rhtas.redhat.com/v1alpha1",
    "kind": "CTlog",
    "metadata": {
      "labels": {
        "app.kubernetes.io/instance": "securesign-sample",
        "app.kubernetes.io/name": "securesign-sample",
        "app.kubernetes.io/part-of": "trusted-artifact-signer"
      },
      "name": "ctlog-sample"
    },
    "spec": null
  },
  {
    "apiVersion": "rhtas.redhat.com/v1alpha1",
    "kind": "Fulcio",
    "metadata": {
      "labels": {
        "app.kubernetes.io/instance": "securesign-sample",
        "app.kubernetes.io/name": "securesign-sample",
        "app.kubernetes.io/part-of": "trusted-artifact-signer"
      },
      "name": "fulcio-sample"
    },
    "spec": {
      "certificate": {
        "commonName": "fulcio.hostname",
        "organizationEmail": "jdoe@redhat.com",
        "organizationName": "Red Hat"
      },
      "config": {
        "OIDCIssuers": {
          "https://your-oidc-issuer-url": {
            "ClientID": "trusted-artifact-signer",
            "IssuerURL": "https://your-oidc-issuer-url",
            "Type": "email"
          }
        }
      },
      "externalAccess": {
        "enabled": true
      }
    }
  },
  {
    "apiVersion": "rhtas.redhat.com/v1alpha1",
    "kind": "Rekor",
    "metadata": {
      "labels": {
        "app.kubernetes.io/instance": "securesign-sample",
        "app.kubernetes.io/name": "securesign-sample",
        "app.kubernetes.io/part-of": "trusted-artifact-signer"
      },
      "name": "rekor-sample"
    },
    "spec": {
      "externalAccess": {
        "enabled": true
      }
    }
  },
  {
    "apiVersion": "rhtas.redhat.com/v1alpha1",
    "kind": "Securesign",
    "metadata": {
      "labels": {
        "app.kubernetes.io/instance": "securesign-sample",
        "app.kubernetes.io/name": "securesign-sample",
        "app.kubernetes.io/part-of": "trusted-artifact-signer"
      },
      "name": "securesign-sample"
    },
    "spec": {
      "ctlog": null,
      "fulcio": {
        "certificate": {
          "commonName": "fulcio.hostname",
          "organizationEmail": "jdoe@redhat.com",
          "organizationName": "Red Hat"
        },
        "config": {
          "OIDCIssuers": {
            "https://your-oidc-issuer-url": {
              "ClientID": "trusted-artifact-signer",
              "IssuerURL": "https://your-oidc-issuer-url",
              "Type": "email"
            }
          }
        },
        "externalAccess": {
          "enabled": true
        },
        "monitoring": false
      },
      "rekor": {
        "externalAccess": {
          "enabled": true
        },
        "monitoring": false
      },
      "trillian": {
        "database": {
          "create": true
        }
      },
      "tuf": {
        "externalAccess": {
          "enabled": true
        }
      }
    }
  },
  {
    "apiVersion": "rhtas.redhat.com/v1alpha1",
    "kind": "Trillian",
    "metadata": {
      "labels": {
        "app.kubernetes.io/instance": "securesign-sample",
        "app.kubernetes.io/name": "securesign-sample",
        "app.kubernetes.io/part-of": "trusted-artifact-signer"
      },
      "name": "trillian-sample"
    },
    "spec": null
  },
  {
    "apiVersion": "rhtas.redhat.com/v1alpha1",
    "kind": "Tuf",
    "metadata": {
      "labels": {
        "app.kubernetes.io/instance": "securesign-sample",
        "app.kubernetes.io/name": "securesign-sample",
        "app.kubernetes.io/part-of": "trusted-artifact-signer"
      },
      "name": "tuf-sample"
    },
    "spec": {
      "external": {
        "enabled": true,
        "host": "tuf.example.com"
      },
      "keys": [
        {
          "name": "rekor.pub",
          "secretRef": {
            "key": "public",
            "name": "rekor-pub-key"
          }
        },
        {
          "name": "fulcio_v1.crt.pem",
          "secretRef": {
            "key": "cert",
            "name": "fulcio-pub-key"
          }
        },
        {
          "name": "ctfe.pub",
          "secretRef": {
            "key": "public",
            "name": "ctlog-pub-key"
          }
        }
      ]
    }
  }
]
